                                            if (validateEmail(email)) {
                                                //alert('cc');
                                                $("#contactdiv").css("display", "none");
                                            } else {
                                                alert('Invalid Email Address');
                                            }

                                            function validateEmail(email) {
                                                //alert('dd');
                                                var filter =
                                                    "/^[\w\-\.\+]+\@[a-zA-Z0-9\.\-]+\.[a-zA-z0-9]{2,4}$/";
                                                if (filter.test(email)) {
                                                    return true;
                                                } else {
                                                    return false;
                                                }
                                            }
                                        }





                                    });
                                });
                            </script>


